public class ForAndForeach {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] y={1,2,4,8,16,32};
		System.out.print("ouput:");
		for (int x : y) {
		System.out.print(x);
		System.out.print(" ");
		}
		String abc="abc";
		System.out.print("abc length "+abc.length());
	}

}
